mapper.select:
select

from zm_course

  and is_delete = 0

    and class_name like CONCAT('%',#{className},'%')


    and teacher_name like CONCAT('%',#{teacherName},'%')


    and course_name like CONCAT('%',#{courseName},'%')


    and start_time >= #{courseStartTime}


    and end_time <= #{courseEndTime}


    and is_effective = #{isEffective}
